import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";
import isLoggedIn from "../../helperFunctions/helperfunctionLog";
import getTableNumber from "../../helperFunctions/helperFunctionTables";
import "./dashboard.css";
import toast, { Toaster } from "react-hot-toast";
import Session from 'react-session-api';


function Dashboard() {
    const [tableNumber, setTableNumber] = useState('');

  //useEffect method
  useEffect(() => {
    setTableNumber(getTableNumber);
  }, []);

  //activate table function
  const activateTable = () => {
    Axios.post("http://localhost:3001/tables/updateTable", {
      table_number: tableNumber,
      status: "online",
    }).then((response) => {
      if (response.data.message === "update successful") {
        Session.set("status", true);  
        setTimeout(() => {
          toast.success(
            "Welcome to Cinta Foods, Your satisfaction is our priority"
          );
        }, 400);
      }
    });
  };
  return !isLoggedIn ? <Redirect to="/login" /> : (
    <div className="container">
      <button
        class="submit"
        type="submit"
        name="status"
        onClick={activateTable}
      >
        <span>Activate Table </span>
      </button>
      <Toaster/>
    </div>
  );
}

export default Dashboard;
