// import useContext (or we could write React.useContext)
import Axios from 'axios';
import React, { useContext, useState } from 'react';
import { AccountContext } from '../accountContext';
import { BoldLink, FormContainer, Input, MutedLink, SubmitButton } from '../common/common';
import { Marginer } from '../marginer';

export function LoginForm(props) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const loginUser = () => {
        Axios.post("http://localhost:3001/login", {email: email, password: password}).then((response) => {

        if(response.data.message) {
            console.log(response.data.message);
        } else {
            console.log(response.data.message);

        }
    });
    }

    /**Call the context changer */
    const { switchToSignup } = useContext(AccountContext);
    return (<div className="CommonBoxContainer">
        <FormContainer>
            <Input type="email" placeholder="Email"
             onChange={(event) => {
                 setEmail(event.target.value)
             } }
             />
            <Input type="password" placeholder="Password"
            onChange={(event) => {
                setPassword(event.target.value)
            }}
            />
        </FormContainer>
        <Marginer direction="vertical" margin={5}/>
        <MutedLink href="#">Forgot Password?</MutedLink>
        <Marginer direction="vertical" margin="1.6em"/>
        <SubmitButton type="submit" onClick={loginUser}>Signin</SubmitButton>
        <Marginer direction="vertical" margin="1em"/>
        <MutedLink>Don't have an account?<BoldLink href="#" onClick={switchToSignup}>SignUp</BoldLink></MutedLink>
    </div>);
}
