import React, { useContext, useState } from 'react';
import { AccountContext } from '../accountContext';
import { BoldLink, CommonBoxContainer, FormContainer, Input, MutedLink, SubmitButton } from '../common/common';
import { Marginer } from '../marginer';
import Axios from 'axios';

export function RegisterForm(props) {

    // creating states
    const [first_name, setFirstName] = useState("");
    const [last_name, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // user creation function
    const register = () => {
        // use axios to post user input
        Axios.post('http://localhost:3001/register', {first_name: first_name, 
        last_name: last_name, email: email, password: password}).then((response) => {
        console.log(response);
    })
    }

    /**Call the context changer */
    const { switchToSignin } = useContext(AccountContext);
    return <CommonBoxContainer>
        <FormContainer>
            <Input type="text" placeholder="Enter your first name"
            onChange={(event) => {
                setFirstName(event.target.value);
            }}/>
            <Input type="text" placeholder="Enter your last name"
            onChange={(event) => {
                setLastName(event.target.value);
            }}/>
            <Input type="email" placeholder="Email"
            onChange={(event) => {
                setEmail(event.target.value);
            }}/>
            <Input type="password" placeholder="Password"
            onChange={(event) => {
                setPassword(event.target.value);
            }}/>
        </FormContainer>
        <Marginer direction="vertical" margin="1.6em"/>
        <SubmitButton type="submit" onClick={register}>Signin</SubmitButton>
        <Marginer direction="vertical" margin="1em"/>
        <MutedLink>Already have an account?<BoldLink href="#" onClick={switchToSignin}>SignIn</BoldLink></MutedLink>
    </CommonBoxContainer>
}
