import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Redirect, useHistory } from "react-router-dom";
import isLoggedIn from "../../helperFunctions/helperfunctionLog";
import getTableNumber from "../../helperFunctions/helperFunctionTables";
import "./dashboard.css";
import toast, { Toaster } from "react-hot-toast";
import Session from 'react-session-api';
import getStatus from "../../helperFunctions/helperFunctionMenu";


function Dashboard() {
    const [tableNumber, setTableNumber] = useState('');
    const history = useHistory();

  //useEffect method
  useEffect(() => {
    setTableNumber(getTableNumber);
    setTimeout( function() {logoutTable(); }, 10000 );
  }, []);

  //logout table
  const logoutTable = () => {
    Session.set("loggedIn", false);
  };
  //activate table function
  const activateTable = () => {
    Axios.patch("http://localhost:3001/tables/updateTable", {
      table_number: 6,
      status: "online",
    }).then((response) => {
      if (response.data.message === "update successful") {
        Session.set("status", true);  
        toast.success( Session.get("status"));
        setTimeout(() => {
          toast.success(
            "Welcome to Cinta Foods, Your satisfaction is our priority"
          );
        }, 400);
        history.push('/menu');
      }
    });
  };
  return !isLoggedIn ? <Redirect to="/login" /> : getStatus ? <Redirect to="/menu" /> : (
    <div className="container">
      <button
        class="submit"
        type="submit"
        name="status"
        onClick={activateTable}
      >
        <span>Activate Table </span>
      </button>
      <Toaster/>
    </div>
  );
}

export default Dashboard;
