import Axios from "axios";
import React, { useEffect } from "react";
import { Redirect } from "react-router-dom";
import { isLoggedIn, getTableNumber } from "../../helperFunctions/helperfunction";
import "./dashboard.css";
import toast, { Toaster } from "react-hot-toast";

function Dashboard() {

  //useEffect method
  useEffect(() => {
    if (!isLoggedIn()) {
      return <Redirect to="/login" />;
    }
    const tableNumber = getTableNumber();
  }, []);

  //activate table function
  const activateTable = () => {
    Axios.post("http://localhost:3001/tables/updateTable", {
      table_number: tableNumber,
      status: "online",
    }).then((response) => {
      if (response.data.message === "update successful") {
        setTimeout(() => {
          toast.success(
            "Welcome to Cinta Foods, Your satisfaction is our priority"
          );
        }, 400);
      }
    });
  };
  return (
    <div className="container">
      <button
        class="submit"
        type="submit"
        name="status"
        onClick={activateTable}
      >
        <span>Activate Table </span>
      </button>
    </div>
  );
}

export default Dashboard;
