// import useContext (or we could write React.useContext)
import Axios from 'axios';
import React, { useState } from 'react';
import { FormContainer, Input, SubmitButton } from '../common/common';
import { Marginer } from '../marginer';
import '../common/common.css';
import Dropdown from 'react-bootstrap/Dropdown';

export function LoginForm(props) {
    const [table_number, settableNumber] = useState("");
    const [password, setPassword] = useState("");

    const loginTable = () => {
        Axios.post("http://localhost:3001/login", {table_number: table_number, password: password}).then((response) => {

        if(response.data.message) {
            console.log(response.data.message);
        } else {
            console.log(response.data.message);

        }
    });
    }

    return (<div className="CommonBoxContainer">
        <FormContainer>
            <Dropdown>
                <Dropdown.ToggleButton>
                    Select table
                </Dropdown.ToggleButton>
            </Dropdown>
            <Input type="text" placeholder="Enter The Table Number"
             onChange={(event) => {
                 settableNumber(event.target.value)
             } }
             />
            <Input type="password" placeholder="Password"
            onChange={(event) => {
                setPassword(event.target.value)
            }}
            />
        </FormContainer>
        <Marginer direction="vertical" margin={5}/>
        <Marginer direction="vertical" margin="1.6em"/>
        <SubmitButton type="submit" onClick={loginTable}>Signin</SubmitButton>
    </div>);
}
