import React from 'react'
import Logo from "./components/Logo";
import Mains from "./components/Mains";
import Extras from "./components/Extras";
import Total from "./components/Total";
import { Provider } from "./Context";
import { mains, sides, drinks } from "./data";
import Axios from 'axios';


export default function Menu() {
  const [mainsList, setMainsList] = useState([]);
  const [drinksList, setDrinksList] = useState([]);
  const [sidesList, setSidesList] = useState([]);

  // get mains list
  const showMains = () => {
    Axios.get('http://localhost:3001/getMains', {
  }).then((response) => {
    setMainsList(response.data)
  });
  };

  // get drinks list
  const showDrinks = () => {
    Axios.get('http://localhost:3001/getDrinks', {
  }).then((response) => {
    setDrinksList(response.data)
  });
  };

  // get sides list
  const showSides = () => {
    Axios.get('http://localhost:3001/getSides', {
  }).then((response) => {
    setSidesList(response.data)
  });
  };

  return (
    <div>
      <Provider>
      <div className="menu">
        <Logo/>
        <Mains meals={showMains} />
        <aside className="aside">
          <Extras type="Sides" items={showSides} />
          <Extras type="Drinks" items={showDrinks} />
        </aside>
        <Total />
      </div>
    </Provider>
    </div>
  )
}
